import { User, ThumbsUp, Clock } from "lucide-react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useQuery } from "@tanstack/react-query";
import { Contribution } from "@shared/schema";

interface Stats {
  plantsIdentified: number;
  contributors: number;
  knowledgeEntries: number;
  languagesSupported: number;
}

export default function CommunitySection() {
  // Fetch app statistics
  const { data: stats } = useQuery<Stats>({
    queryKey: ['/api/stats'],
    staleTime: 60000, // Cache for 1 minute
  });

  // Fetch recent contributions
  const { data: contributions = [] } = useQuery<Contribution[]>({
    queryKey: ['/api/contributions'],
    staleTime: 30000, // Cache for 30 seconds
  });

  const formatTimeAgo = (date: Date | string) => {
    const now = new Date();
    const contributionDate = new Date(date);
    const diffInHours = Math.floor((now.getTime() - contributionDate.getTime()) / (1000 * 60 * 60));

    if (diffInHours < 1) return "Just now";
    if (diffInHours === 1) return "1 hour ago";
    if (diffInHours < 24) return `${diffInHours} hours ago`;

    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays === 1) return "1 day ago";
    return `${diffInDays} days ago`;
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'approved':
        return 'bg-primary/10 text-primary';
      case 'pending':
        return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200';
      case 'rejected':
        return 'bg-destructive/10 text-destructive';
      default:
        return 'bg-muted text-muted-foreground';
    }
  };

  return (
    <section id="community" className="bg-muted/30 py-16 px-4">
      <div className="max-w-6xl mx-auto">
        <div className="text-center mb-12">
          <h3 className="text-3xl font-bold text-foreground mb-4" data-testid="community-title">
            Community Impact
          </h3>
          <p className="text-muted-foreground" data-testid="community-description">
            Together we're preserving traditional knowledge for future generations
          </p>
        </div>

        {/* Stats Grid */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-8 mb-12">
          <div className="text-center" data-testid="stat-plants-identified">
            <div className="text-4xl font-bold text-primary mb-2">
              {stats?.plantsIdentified || 0}
            </div>
            <p className="text-muted-foreground">Plants Identified</p>
          </div>
          <div className="text-center" data-testid="stat-contributors">
            <div className="text-4xl font-bold text-accent mb-2">
              {stats?.contributors || 0}
            </div>
            <p className="text-muted-foreground">Contributors</p>
          </div>
          <div className="text-center" data-testid="stat-knowledge-entries">
            <div className="text-4xl font-bold text-primary mb-2">
              {stats?.knowledgeEntries || 0}
            </div>
            <p className="text-muted-foreground">Knowledge Entries</p>
          </div>
          <div className="text-center" data-testid="stat-languages">
            <div className="text-4xl font-bold text-accent mb-2">
              {stats?.languagesSupported || 0}
            </div>
            <p className="text-muted-foreground">Languages</p>
          </div>
        </div>

        {/* Recent Contributions */}
        <Card className="bg-card border border-border rounded-xl">
          <CardContent className="p-8">
            <h4 className="text-xl font-semibold text-foreground mb-6" data-testid="recent-contributions-title">
              Recent Contributions
            </h4>
            <div className="space-y-6">
              {contributions.length === 0 ? (
                <div className="text-center py-8" data-testid="no-contributions">
                  <p className="text-muted-foreground">No contributions yet. Be the first to share your knowledge!</p>
                </div>
              ) : (
                contributions.slice(0, 3).map((contribution) => (
                  <div 
                    key={contribution.id} 
                    className="flex items-start space-x-4 p-4 bg-muted/50 rounded-lg"
                    data-testid={`contribution-${contribution.id}`}
                  >
                    <div className="w-12 h-12 bg-primary/10 rounded-full flex items-center justify-center">
                      <User className="text-primary h-5 w-5" />
                    </div>
                    <div className="flex-1">
                      <div className="flex items-center space-x-2 mb-2">
                        <span className="font-medium text-foreground" data-testid={`contributor-name-${contribution.id}`}>
                          {contribution.contributorName}
                        </span>
                        <span className="text-sm text-muted-foreground">‚Ä¢</span>
                        <span className="text-sm text-muted-foreground flex items-center" data-testid={`contribution-time-${contribution.id}`}>
                          <Clock className="h-3 w-3 mr-1" />
                          {formatTimeAgo(contribution.createdAt!)}
                        </span>
                      </div>
                      <p className="text-foreground mb-2" data-testid={`contribution-content-${contribution.id}`}>
                        Shared knowledge about traditional plant medicine
                      </p>
                      <p className="text-sm text-muted-foreground mb-3" data-testid={`contribution-summary-${contribution.id}`}>
                        {contribution.content.length > 100 
                          ? `${contribution.content.substring(0, 100)}...` 
                          : contribution.content
                        }
                      </p>
                      <div className="flex items-center space-x-2">
                        <Badge 
                          className={getStatusColor(contribution.status || 'pending')}
                          data-testid={`contribution-status-${contribution.id}`}
                        >
                          {contribution.status === 'approved' ? 'Verified' : 
                           contribution.status === 'pending' ? 'Under Review' : 
                           'Rejected'}
                        </Badge>
                        <Button 
                          variant="ghost" 
                          size="sm"
                          className="text-sm text-muted-foreground hover:text-primary transition-colors"
                          data-testid={`like-button-${contribution.id}`}
                        >
                          <ThumbsUp className="h-3 w-3 mr-1" />
                          {Math.floor(Math.random() * 20) + 1}
                        </Button>
                      </div>
                    </div>
                  </div>
                ))
              )}

              {contributions.length > 0 && (
                <div className="text-center pt-4">
                  <Button 
                    variant="link" 
                    className="text-primary hover:text-primary/80 font-medium transition-colors"
                    data-testid="view-all-contributions"
                  >
                    View All Contributions ‚Üí
                  </Button>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </section>
  );
}

client/src/components/contribution-form..content:
import { useState } from "react";
import { Upload, Send, Mic } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertContributionSchema, insertPlantSchema } from "@shared/schema";
import { z } from "zod";
import VoiceRecorder from "@/components/voice-recorder";

const contributionFormSchema = insertPlantSchema.extend({
  contributorName: z.string().min(1, "Name is required"),
});

type ContributionFormData = z.infer<typeof contributionFormSchema>;

export default function ContributionForm() {
  const [selectedFiles, setSelectedFiles] = useState<File[]>([]);
  const [audioBlob, setAudioBlob] = useState<Blob | null>(null);
  const [transcript, setTranscript] = useState("");
  const [dragActive, setDragActive] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const {
    register,
    handleSubmit,
    reset,
    getValues,
    setValue,
    formState: { errors, isSubmitting },
  } = useForm<ContributionFormData>({
    resolver: zodResolver(contributionFormSchema),
    defaultValues: {
      name: "",
      scientificName: "",
      description: "",
      uses: "",
      preparation: "",
      location: "",
      contributorName: "",
    },
  });

  const contributionMutation = useMutation({
    mutationFn: async (data: ContributionFormData) => {
      // Create the plant entry
      const response = await apiRequest('POST', '/api/plants', {
        name: data.name,
        scientificName: data.scientificName,
        description: data.description,
        uses: data.uses,
        preparation: data.preparation,
        location: data.location,
        contributorId: null, // Anonymous for now
      });

      const plant = await response.json();

      // Create a contribution record
      await apiRequest('POST', '/api/contributions', {
        plantId: plant.id,
        contributorId: null,
        contributorName: data.contributorName,
        type: 'knowledge',
        content: `Plant: ${data.name}. Uses: ${data.uses}. ${data.preparation ? `Preparation: ${data.preparation}` : ''}`,
      });

      return plant;
    },
    onSuccess: () => {
      toast({
        title: "Contribution Submitted!",
        description: "Thank you for sharing your knowledge. It will be reviewed shortly.",
      });
      reset();
      setSelectedFiles([]);
      // Invalidate relevant queries
      queryClient.invalidateQueries({ queryKey: ['/api/plants'] });
      queryClient.invalidateQueries({ queryKey: ['/api/contributions'] });
    },
    onError: () => {
      toast({
        title: "Submission Failed",
        description: "Unable to submit your contribution. Please try again.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: ContributionFormData) => {
    contributionMutation.mutate(data);
  };

  const handleDrag = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files) {
      const files = Array.from(e.dataTransfer.files).filter(file => 
        file.type.startsWith('image/')
      );
      setSelectedFiles(prev => [...prev, ...files]);
    }
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      const files = Array.from(e.target.files).filter(file => 
        file.type.startsWith('image/')
      );
      setSelectedFiles(prev => [...prev, ...files]);
    }
  };

  const handleRecordingComplete = (audioBlob: Blob, transcriptText?: string) => {
    setAudioBlob(audioBlob);
    if (transcriptText) {
      setTranscript(transcriptText);
      // Auto-fill form fields from transcript if they're empty
      const words = transcriptText.toLowerCase();
      if (!getValues('uses') && words.includes('use')) {
        // Extract uses information from transcript
        const usesMatch = transcriptText.match(/(?:use|treat|heal|cure)[sd]?\s+(?:for|to)?\s+([^.]+)/i);
        if (usesMatch) {
          setValue('uses', usesMatch[1].trim());
        }
      }
    }
  };

  const handleTranscriptChange = (newTranscript: string) => {
    setTranscript(newTranscript);
  };

  return (
    <section id="contribute" className="py-16 px-4">
      <div className="max-w-4xl mx-auto">
        <div className="text-center mb-12">
          <h3 className="text-3xl font-bold text-foreground mb-4" data-testid="contribution-title">
            Share Your Knowledge
          </h3>
          <p className="text-muted-foreground" data-testid="contribution-description">
            Help preserve traditional medicinal plant wisdom by contributing to our community database
          </p>
        </div>

        <Card className="bg-card border border-border rounded-xl">
          <CardContent className="p-8">
            <form onSubmit={handleSubmit(onSubmit)} className="space-y-6" data-testid="contribution-form">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <Label htmlFor="name" className="block text-sm font-medium text-foreground mb-2">
                    Plant Name *
                  </Label>
                  <Input
                    id="name"
                    {...register("name")}
                    placeholder="Common or local name"
                    className="w-full"
                    data-testid="input-plant-name"
                  />
                  {errors.name && (
                    <p className="text-sm text-destructive mt-1" data-testid="error-plant-name">
                      {errors.name.message}
                    </p>
                  )}
                </div>
                <div>
                  <Label htmlFor="scientificName" className="block text-sm font-medium text-foreground mb-2">
                    Scientific Name
                  </Label>
                  <Input
                    id="scientificName"
                    {...register("scientificName")}
                    placeholder="Botanical name (if known)"
                    className="w-full"
                    data-testid="input-scientific-name"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="uses" className="block text-sm font-medium text-foreground mb-2">
                  Medicinal Uses *
                </Label>
                <Textarea
                  id="uses"
                  {...register("uses")}
                  rows={4}
                  placeholder="Describe traditional uses, treatments, and benefits..."
                  className="w-full"
                  data-testid="textarea-uses"
                />
                {errors.uses && (
                  <p className="text-sm text-destructive mt-1" data-testid="error-uses">
                    {errors.uses.message}
                  </p>
                )}
              </div>

              <div>
                <Label htmlFor="preparation" className="block text-sm font-medium text-foreground mb-2">
                  Preparation Methods
                </Label>
                <Textarea
                  id="preparation"
                  {...register("preparation")}
                  rows={3}
                  placeholder="How is this plant prepared and used? (e.g., tea, paste, fresh consumption)"
                  className="w-full"
                  data-testid="textarea-preparation"
                />
              </div>

              <div>
                <Label className="block text-sm font-medium text-foreground mb-2">
                  Plant Images
                </Label>
                <div 
                  className={`border-2 border-dashed rounded-lg p-6 text-center hover:border-primary transition-colors cursor-pointer ${dragActive ? 'border-primary bg-primary/5' : 'border-border'}`}
                  onDragEnter={handleDrag}
                  onDragLeave={handleDrag}
                  onDragOver={handleDrag}
                  onDrop={handleDrop}
                  onClick={() => document.getElementById('image-input')?.click()}
                  data-testid="image-upload-area"
                >
                  <Upload className="mx-auto h-8 w-8 text-muted-foreground mb-4" />
                  <p className="text-muted-foreground">Drop images here or click to upload</p>
                  <p className="text-sm text-muted-foreground mt-2">Multiple angles recommended</p>
                  {selectedFiles.length > 0 && (
                    <p className="text-sm text-primary mt-2" data-testid="selected-files-count">
                      {selectedFiles.length} file(s) selected
                    </p>
                  )}
                </div>
                <input
                  id="image-input"
                  type="file"
                  accept="image/*"
                  multiple
                  onChange={handleFileChange}
                  className="hidden"
                  data-testid="image-input"
                />
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <Label htmlFor="contributorName" className="block text-sm font-medium text-foreground mb-2">
                    Your Name *
                  </Label>
                  <Input
                    id="contributorName"
                    {...register("contributorName")}
                    placeholder="For attribution"
                    className="w-full"
                    data-testid="input-contributor-name"
                  />
                  {errors.contributorName && (
                    <p className="text-sm text-destructive mt-1" data-testid="error-contributor-name">
                      {errors.contributorName.message}
                    </p>
                  )}
                </div>
                <div>
                  <Label htmlFor="location" className="block text-sm font-medium text-foreground mb-2">
                    Location
                  </Label>
                  <Input
                    id="location"
                    {...register("location")}
                    placeholder="Region where plant is found"
                    className="w-full"
                    data-testid="input-location"
                  />
                </div>
              </div>

              <VoiceRecorder 
                onRecordingComplete={handleRecordingComplete}
                onTranscriptChange={handleTranscriptChange}
                className="bg-muted rounded-lg"
              />

              {transcript && (
                <Card className="bg-primary/5 border-primary/20">
                  <CardContent className="p-4">
                    <h5 className="font-medium text-foreground mb-2">Transcribed Content:</h5>
                    <p className="text-sm text-muted-foreground mb-3">{transcript}</p>
                    <Button
                      type="button"
                      onClick={() => {
                        if (!getValues('uses')) {
                          setValue('uses', transcript);
                        } else {
                          setValue('uses', getValues('uses') + '\n\n' + transcript);
                        }
                      }}
                      variant="outline"
                      size="sm"
                      data-testid="apply-transcript-button"
                    >
                      Apply to Form
                    </Button>
                  </CardContent>
                </Card>
              )}

              <div className="flex items-center space-x-4">
                <Button 
                  type="submit" 
                  disabled={isSubmitting}
                  className="bg-primary text-primary-foreground px-8 py-3 rounded-lg font-medium hover:bg-primary/90 transition-colors"
                  data-testid="submit-button"
                >
                  <Send className="mr-2 h-4 w-4" />
                  {isSubmitting ? "Submitting..." : "Submit Contribution"}
                </Button>
                <p className="text-sm text-muted-foreground">
                  Contributions are reviewed before publishing
                </p>
              </div>
            </form>
          </CardContent>
        </Card>
      </div>
    </section>
  );
}

client/src/components/header.tsx.content:
import { useState } from "react";
import { Leaf, Moon, Sun, Menu, X, User, LogOut, Settings, Download, Shield } from "lucide-react";
import { Button } from "@/components/ui/button";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuSeparator, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Badge } from "@/components/ui/badge";
import { useAuth } from "@/contexts/auth-context";
import { useLanguage } from "@/contexts/language-context";
import AuthModal from "@/components/auth/auth-modal";
import LanguageSelector from "@/components/language-selector";
import { useLocation } from "wouter";

export default function Header() {
  const [isDark, setIsDark] = useState(document.documentElement.classList.contains('dark'));
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isAuthModalOpen, setIsAuthModalOpen] = useState(false);
  const [authModalTab, setAuthModalTab] = useState<'login' | 'register'>('login');
  const { user, logout } = useAuth();
  const { t } = useLanguage();
  const [location, setLocation] = useLocation();

  const handleDownloadStatic = async () => {
    try {
      const response = await fetch('/api/download-static');
      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'mediplant-static-website.tar.gz';
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
      } else {
        console.error('Failed to download static website');
      }
    } catch (error) {
      console.error('Error downloading static website:', error);
    }
  };

  const toggleTheme = () => {
    const newTheme = !isDark;
    setIsDark(newTheme);
    document.documentElement.classList.toggle('dark', newTheme);
    localStorage.setItem('theme', newTheme ? 'dark' : 'light');
  };

  const scrollToSection = (id: string) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    setIsMobileMenuOpen(false);
  };

  return (
    <header className="bg-card border-b border-border shadow-sm sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          <div className="flex items-center space-x-3">
            <div className="w-10 h-10 bg-primary rounded-lg flex items-center justify-center">
              <Leaf className="text-primary-foreground text-lg" data-testid="logo-icon" />
            </div>
            <div>
              <h1 className="text-xl font-bold text-foreground" data-testid="site-title">{t('site.title')}</h1>
              <p className="text-xs text-muted-foreground">{t('site.tagline')}</p>
            </div>
          </div>

          <nav className="hidden md:flex items-center space-x-8">
            <button 
              onClick={() => scrollToSection('identify')}
              className="text-foreground hover:text-primary font-medium transition-colors"
              data-testid="nav-identify"
            >
              {t('nav.identify')}
            </button>
            <button 
              onClick={() => scrollToSection('knowledge')}
              className="text-foreground hover:text-primary font-medium transition-colors"
              data-testid="nav-knowledge"
            >
              {t('nav.knowledge')}
            </button>
            <button 
              onClick={() => scrollToSection('contribute')}
              className="text-foreground hover:text-primary font-medium transition-colors"
              data-testid="nav-contribute"
            >
              {t('nav.contribute')}
            </button>
            <button 
              onClick={() => scrollToSection('community')}
              className="text-foreground hover:text-primary font-medium transition-colors"
              data-testid="nav-community"
            >
              {t('nav.community')}
            </button>
          </nav>

          <div className="flex items-center space-x-4">
            {user ? (
              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button variant="outline" className="flex items-center space-x-2" data-testid="user-menu">
                    <User className="h-4 w-4" />
                    <span className="hidden sm:inline">{user.name}</span>
                    {user.isAdmin && (
                      <Badge variant="secondary" className="ml-2 text-xs">Admin</Badge>
                    )}
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent align="end" className="w-56">
                  <DropdownMenuItem className="flex items-center space-x-2">
                    <User className="h-4 w-4" />
                    <div className="flex flex-col">
                      <span className="font-medium">{user.name}</span>
                      <span className="text-xs text-muted-foreground">{user.email}</span>
                    </div>
                  </DropdownMenuItem>
                  {user.badges && user.badges.length > 0 && (
                    <DropdownMenuItem className="flex flex-wrap gap-1">
                      {user.badges.map((badge) => (
                        <Badge key={badge} variant="outline" className="text-xs">
                          {badge}
                        </Badge>
                      ))}
                    </DropdownMenuItem>
                  )}
                  <DropdownMenuSeparator />
                  <DropdownMenuItem className="flex items-center space-x-2">
                    <Settings className="h-4 w-4" />
                    <span>Profile Settings</span>
                  </DropdownMenuItem>
                  {user.isAdmin && (
                    <DropdownMenuItem 
                      onClick={() => setLocation('/admin')}
                      className="flex items-center space-x-2 cursor-pointer" 
                      data-testid="admin-panel-link"
                    >
                      <Shield className="h-4 w-4" />
                      <span>Admin Panel</span>
                    </DropdownMenuItem>
                  )}
                  <DropdownMenuSeparator />
                  <DropdownMenuItem 
                    onClick={logout}
                    className="flex items-center space-x-2 text-destructive focus:text-destructive"
                    data-testid="logout-button"
                  >
                    <LogOut className="h-4 w-4" />
                    <span>Sign Out</span>
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            ) : (
              <div className="flex items-center space-x-2">
                <Button
                  variant="ghost"
                  onClick={() => {
                    setAuthModalTab('login');
                    setIsAuthModalOpen(true);
                  }}
                  data-testid="login-button"
                >
                  {t('auth.signIn')}
                </Button>
                <Button
                  onClick={() => {
                    setAuthModalTab('register');
                    setIsAuthModalOpen(true);
                  }}
                  data-testid="register-button"
                >
                  {t('auth.signUp')}
                </Button>
              </div>
            )}

            <LanguageSelector />

            <Button
              variant="outline"
              size="icon"
              onClick={handleDownloadStatic}
              title="Download Static Website"
              data-testid="download-static"
            >
              <Download className="h-4 w-4" />
            </Button>

            <Button
              variant="outline"
              size="icon"
              onClick={toggleTheme}
              data-testid="theme-toggle"
            >
              {isDark ? <Sun className="h-4 w-4" /> : <Moon className="h-4 w-4" />}
            </Button>
            <Button
              variant="outline"
              size="icon"
              className="md:hidden"
              onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              data-testid="mobile-menu-toggle"
            >
              {isMobileMenuOpen ? <X className="h-4 w-4" /> : <Menu className="h-4 w-4" />}
            </Button>
          </div>
        </div>

        {/* Mobile menu */}
        {isMobileMenuOpen && (
          <div className="md:hidden border-t border-border py-4" data-testid="mobile-menu">
            <nav className="flex flex-col space-y-4">
              <button 
                onClick={() => scrollToSection('identify')}
                className="text-left text-foreground hover:text-primary font-medium transition-colors"
                data-testid="mobile-nav-identify"
              >
                Identify
              </button>
              <button 
                onClick={() => scrollToSection('knowledge')}
                className="text-left text-foreground hover:text-primary font-medium transition-colors"
                data-testid="mobile-nav-knowledge"
              >
                Knowledge Base
              </button>
              <button 
                onClick={() => scrollToSection('contribute')}
                className="text-left text-foreground hover:text-primary font-medium transition-colors"
                data-testid="mobile-nav-contribute"
              >
                Contribute
              </button>
              <button 
                onClick={() => scrollToSection('community')}
                className="text-left text-foreground hover:text-primary font-medium transition-colors"
                data-testid="mobile-nav-community"
              >
                Community
              </button>
              {user?.isAdmin && (
                <button 
                  onClick={() => {
                    setLocation('/admin');
                    setIsMobileMenuOpen(false);
                  }}
                  className="text-left text-primary hover:text-primary-hover font-medium transition-colors flex items-center space-x-2"
                  data-testid="mobile-nav-admin"
                >
                  <Shield className="h-4 w-4" />
                  <span>Admin Panel</span>
                </button>
              )}
            </nav>
          </div>
        )}
      </div>

      <AuthModal 
        isOpen={isAuthModalOpen}
        onClose={() => setIsAuthModalOpen(false)}
        defaultTab={authModalTab}
      />
    </header>
  );
}

client/src/components/knowledge-search.t.content:
import { useState } from "react";
import { Search, Leaf, Plus, Volume2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent } from "@/components/ui/card";
import { Dialog, DialogContent, DialogTrigger } from "@/components/ui/dialog";
import AudioButton from "@/components/audio-button";
import KnowledgeContribution from "@/components/knowledge-contribution";
import { useQuery } from "@tanstack/react-query";
import { Plant } from "@shared/schema";
import { useLanguage } from "@/contexts/language-context";

export default function KnowledgeSearch() {
  const [searchQuery, setSearchQuery] = useState("");
  const [showResults, setShowResults] = useState(false);
  const [showContributionForm, setShowContributionForm] = useState(false);
  const { t, language } = useLanguage();

  // Fetch all plants
  const { data: plants = [], isLoading } = useQuery<Plant[]>({
    queryKey: ['/api/plants'],
  });

  // Search plants
  const { data: searchResults = [] } = useQuery<Plant[]>({
    queryKey: ['/api/plants/search', searchQuery],
    queryFn: async () => {
      if (!searchQuery || searchQuery.length < 2) return [];
      const response = await fetch(`/api/plants/search?q=${encodeURIComponent(searchQuery)}`);
      return response.json();
    },
    enabled: searchQuery.length >= 2,
  });

  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    setSearchQuery(value);
    setShowResults(value.length >= 2);
  };

  const handleSearchSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchQuery.length >= 2) {
      setShowResults(true);
    }
  };

  // Featured plants (first 3 from all plants)
  const featuredPlants = plants.slice(0, 3);

  return (
    <section id="knowledge" className="bg-muted/30 py-16 px-4">
      <div className="max-w-6xl mx-auto">
        <div className="text-center mb-12">
          <div className="flex items-center justify-between mb-8">
            <div className="text-center flex-1">
              <h3 className="text-3xl font-bold text-foreground mb-4" data-testid="search-title">
                {t('knowledge.searchTitle') || 'Search Plant Knowledge'}
              </h3>
              <p className="text-muted-foreground" data-testid="search-description">
                {t('knowledge.searchDescription') || 'Explore our community-driven database of medicinal plants'}
              </p>
            </div>

            <Dialog open={showContributionForm} onOpenChange={setShowContributionForm}>
              <DialogTrigger asChild>
                <Button className="bg-primary hover:bg-primary/90" data-testid="add-knowledge-button">
                  <Plus className="h-4 w-4 mr-2" />
                  {t('knowledge.addNew') || 'Add Knowledge'}
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
                <KnowledgeContribution onClose={() => setShowContributionForm(false)} />
              </DialogContent>
            </Dialog>
          </div>
        </div>

        <div className="search-container max-w-2xl mx-auto mb-12">
          <form onSubmit={handleSearchSubmit} className="relative">
            <Input
              type="text"
              placeholder={language === 'hi' ? '‡§™‡•å‡§ß‡•á ‡§ï‡§æ ‡§®‡§æ‡§Æ, ‡§î‡§∑‡§ß‡•Ä‡§Ø ‡§â‡§™‡§Ø‡•ã‡§ó ‡§Ø‡§æ ‡§≤‡§ï‡•ç‡§∑‡§£ ‡§∏‡•á ‡§ñ‡•ã‡§ú‡•á‡§Ç...' : 'Search by plant name, medicinal use, or symptom...'}
              value={searchQuery}
              onChange={handleSearchChange}
              className="w-full px-6 py-4 pr-12 bg-card border border-border rounded-xl text-foreground placeholder-muted-foreground focus:outline-none focus:ring-2 focus:ring-ring"
              data-testid="search-input"
            />
            <Button
              type="submit"
              variant="ghost"
              className="absolute right-4 top-1/2 transform -translate-y-1/2 text-muted-foreground hover:text-primary"
              data-testid="search-button"
            >
              <Search className="h-4 w-4" />
            </Button>
          </form>

          {showResults && searchResults.length > 0 && (
            <div className="search-results mt-2" data-testid="search-results">
              {searchResults.slice(0, 10).map((plant) => (
                <div key={plant.id} className="p-3 hover:bg-muted cursor-pointer border-b border-border last:border-b-0">
                  <div className="flex items-center space-x-3">
                    {plant.imageUrl ? (
                      <img 
                        src={plant.imageUrl}
                        alt={plant.name}
                        className="w-12 h-12 rounded-lg object-cover"
                        onError={(e) => {
                          e.currentTarget.style.display = 'none';
                          const leafIcon = document.createElement('div');
                          leafIcon.innerHTML = '<svg className="w-4 h-4"><use href="#leaf-icon"></use></svg>';
                          e.currentTarget.parentNode?.appendChild(leafIcon);
                        }}
                      />
                    ) : (
                      <Leaf className="text-primary h-4 w-4" />
                    )}
                    <div className="flex-1">
                      <div className="flex items-center justify-between">
                        <div>
                          <p className="font-medium text-foreground" data-testid={`result-name-${plant.id}`}>
                            {plant.name}
                            {plant.hindiName && plant.hindiName !== plant.name && (
                              <span className="ml-2 text-sm font-normal text-muted-foreground">
                                ({plant.hindiName})
                              </span>
                            )}
                            {plant.sanskritName && (
                              <span className="ml-2 text-xs italic text-muted-foreground">
                                [{plant.sanskritName}]
                              </span>
                            )}
                          </p>
                          <p className="text-xs text-muted-foreground mb-1" data-testid={`result-scientific-${plant.id}`}>
                            {plant.scientificName}
                          </p>
                          <p className="text-sm text-muted-foreground" data-testid={`result-uses-${plant.id}`}>
                            {language === 'hi' && plant.hindiUses ? plant.hindiUses : plant.uses}
                          </p>
                        </div>
                        <button
                          onClick={() => {
                            const text = `${plant.name}. ${language === 'hi' && plant.hindiDescription ? plant.hindiDescription : plant.description}`;
                            const speech = new SpeechSynthesisUtterance(text);
                            speech.lang = language === 'hi' ? 'hi-IN' : 'en-US';
                            speechSynthesis.speak(speech);
                          }}
                          className="p-1 hover:bg-muted-foreground/10 rounded"
                        >
                          <Volume2 className="h-4 w-4 text-muted-foreground" />
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Featured Plants Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {isLoading ? (
            // Loading skeleton
            Array.from({ length: 3 }).map((_, index) => (
              <Card key={index} className="plant-card bg-card border border-border rounded-xl overflow-hidden">
                <div className="w-full h-48 bg-muted animate-pulse" />
                <CardContent className="p-6">
                  <div className="space-y-3">
                    <div className="h-6 bg-muted rounded animate-pulse" />
                    <div className="h-4 bg-muted rounded animate-pulse w-3/4" />
                    <div className="h-16 bg-muted rounded animate-pulse" />
                    <div className="flex justify-between items-center">
                      <div className="h-8 bg-muted rounded animate-pulse w-20" />
                      <div className="h-4 bg-muted rounded animate-pulse w-16" />
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))
          ) : (
            featuredPlants.map((plant) => (
              <Card key={plant.id} className="plant-card bg-card border border-border rounded-xl overflow-hidden" data-testid={`plant-card-${plant.id}`}>
                <img 
                  src={plant.imageUrl || "https://images.unsplash.com/photo-1441974231531-c6227db76b6e"} 
                  alt={`${plant.name} medicinal plant`} 
                  className="w-full h-48 object-cover"
                  data-testid={`plant-image-${plant.id}`}
                />
                <CardContent className="p-6">
                  <div className="mb-3">
                    <h4 className="text-xl font-semibold text-foreground mb-1" data-testid={`plant-name-${plant.id}`}>
                      {plant.name}
                    </h4>
                    {plant.hindiName && (
                      <p className="text-lg font-medium text-muted-foreground mb-1" data-testid={`plant-hindi-name-${plant.id}`}>
                        {plant.hindiName}
                      </p>
                    )}
                    {plant.sanskritName && (
                      <p className="text-sm text-muted-foreground italic mb-2" data-testid={`plant-sanskrit-name-${plant.id}`}>
                        Sanskrit: {plant.sanskritName}
                      </p>
                    )}
                    <p className="text-sm text-muted-foreground" data-testid={`plant-scientific-name-${plant.id}`}>
                      {plant.scientificName}
                    </p>
                  </div>
                  <div className="space-y-3">
                    <div>
                      <span className="text-sm font-medium text-foreground">
                        {language === 'hi' ? '‡§â‡§™‡§Ø‡•ã‡§ó:' : 'Uses:'}
                      </span>
                      <p className="text-sm text-muted-foreground" data-testid={`plant-uses-${plant.id}`}>
                        {language === 'hi' && plant.hindiUses ? plant.hindiUses : plant.uses}
                      </p>
                    </div>
                    {plant.regionalNames && (
                      <div>
                        <span className="text-sm font-medium text-foreground">Regional Names:</span>
                        <p className="text-xs text-muted-foreground" data-testid={`plant-regional-names-${plant.id}`}>
                          {plant.regionalNames}
                        </p>
                      </div>
                    )}
                    <div className="flex items-center justify-between">
                      <AudioButton 
                        text={`${plant.name}. ${language === 'hi' && plant.hindiDescription ? plant.hindiDescription : (plant.description || 'A medicinal plant')}. Traditional uses include: ${language === 'hi' && plant.hindiUses ? plant.hindiUses : plant.uses}`}
                        variant="primary"
                        size="sm"
                        data-testid={`audio-button-${plant.id}`}
                      />
                      <span className="text-xs text-muted-foreground" data-testid={`contributors-count-${plant.id}`}>
                        {Math.floor(Math.random() * 10) + 1} contributors
                      </span>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))
          )}
        </div>
      </div>
    </section>
  );
}

client/src/components/language-selector..content:
import { Globe } from 'lucide-react';
import { Button } from '@/components/ui/button';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import { useLanguage } from '@/contexts/language-context';

export default function LanguageSelector() {
  const { language, setLanguage, languages } = useLanguage();

  const currentLanguage = languages.find(lang => lang.code === language);

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button
          variant="outline"
          size="icon"
          className="flex items-center"
          data-testid="language-selector"
        >
          <Globe className="h-4 w-4" />
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end" className="w-56">
        {languages.map((lang) => (
          <DropdownMenuItem
            key={lang.code}
            onClick={() => setLanguage(lang.code)}
            className={`flex items-center justify-between cursor-pointer ${
              language === lang.code ? 'bg-accent' : ''
            }`}
            data-testid={`language-option-${lang.code}`}
          >
            <span className="flex items-center space-x-2">
              <span>{lang.nativeName}</span>
              {lang.name !== lang.nativeName && (
                <span className="text-sm text-muted-foreground">({lang.name})</span>
              )}
            </span>
            {language === lang.code && (
              <span className="text-primary">‚úì</span>
            )}
          </DropdownMenuItem>
        ))}
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

