import { useState } from "react";
import { Plus, Leaf, Save } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useLanguage } from "@/contexts/language-context";
import { insertPlantSchema } from "@shared/schema";
import { z } from "zod";

const knowledgeFormSchema = insertPlantSchema.extend({
  contributorName: z.string().min(1, "Contributor name is required"),
});

type KnowledgeFormData = z.infer<typeof knowledgeFormSchema>;

interface KnowledgeContributionProps {
  onClose?: () => void;
}

export default function KnowledgeContribution({ onClose }: KnowledgeContributionProps) {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { toast } = useToast();
  const { t } = useLanguage();
  const queryClient = useQueryClient();

  const form = useForm<KnowledgeFormData>({
    resolver: zodResolver(knowledgeFormSchema),
    defaultValues: {
      name: "",
      scientificName: "",
      description: "",
      uses: "",
      preparation: "",
      location: "",
      imageUrl: "",
      contributorName: "",
      verificationStatus: "pending",
    },
  });

  const submitKnowledgeMutation = useMutation({
    mutationFn: async (data: KnowledgeFormData) => {
      return apiRequest('POST', '/api/plants', data);
    },
    onSuccess: () => {
      toast({
        title: t('knowledge.submitted') || 'Knowledge Submitted',
        description: t('knowledge.submittedDesc') || 'Your plant knowledge has been submitted for review.',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/plants'] });
      form.reset();
      onClose?.();
    },
    onError: (error) => {
      toast({
        title: t('knowledge.error') || 'Submission Error',
        description: error.message || 'Failed to submit knowledge',
        variant: 'destructive',
      });
    },
    onSettled: () => {
      setIsSubmitting(false);
    }
  });

  const onSubmit = (data: KnowledgeFormData) => {
    setIsSubmitting(true);
    submitKnowledgeMutation.mutate(data);
  };

  return (
    <Card className="w-full max-w-2xl mx-auto bg-card border border-border" data-testid="knowledge-contribution-form">
      <CardHeader>
        <CardTitle className="flex items-center space-x-2 text-foreground">
          <Leaf className="h-5 w-5 text-primary" />
          <span>{t('knowledge.addPlant') || 'Add Plant Knowledge'}</span>
        </CardTitle>
      </CardHeader>

      <CardContent>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="name"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>{t('knowledge.plantName') || 'Plant Name'} *</FormLabel>
                    <FormControl>
                      <Input 
                        placeholder={t('knowledge.plantNamePlaceholder') || 'e.g., Turmeric'}
                        {...field}
                        data-testid="input-plant-name"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="scientificName"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>{t('knowledge.scientificName') || 'Scientific Name'}</FormLabel>
                    <FormControl>
                      <Input 
                        placeholder={t('knowledge.scientificNamePlaceholder') || 'e.g., Curcuma longa'}
                        value={field.value || ""}
                        onChange={field.onChange}
                        onBlur={field.onBlur}
                        name={field.name}
                        ref={field.ref}
                        data-testid="input-scientific-name"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>{t('knowledge.description') || 'Description'}</FormLabel>
                  <FormControl>
                    <Textarea 
                      placeholder={t('knowledge.descriptionPlaceholder') || 'Describe the plant\'s appearance, habitat, and characteristics...'}
                      rows={3}
                      value={field.value || ""}
                      onChange={field.onChange}
                      onBlur={field.onBlur}
                      name={field.name}
                      ref={field.ref}
                      data-testid="textarea-description"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="uses"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>{t('knowledge.medicinalUses') || 'Medicinal Uses'} *</FormLabel>
                  <FormControl>
                    <Textarea 
                      placeholder={t('knowledge.usesPlaceholder') || 'List traditional medicinal uses, benefits, and applications...'}
                      rows={3}
                      {...field}
                      data-testid="textarea-uses"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="preparation"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>{t('knowledge.preparation') || 'Preparation Methods'}</FormLabel>
                  <FormControl>
                    <Textarea 
                      placeholder={t('knowledge.preparationPlaceholder') || 'How to prepare and use this plant for medicinal purposes...'}
                      rows={2}
                      value={field.value || ""}
                      onChange={field.onChange}
                      onBlur={field.onBlur}
                      name={field.name}
                      ref={field.ref}
                      data-testid="textarea-preparation"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="location"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>{t('knowledge.location') || 'Found In'}</FormLabel>
                    <FormControl>
                      <Input 
                        placeholder={t('knowledge.locationPlaceholder') || 'Region or habitat where it grows'}
                        value={field.value || ""}
                        onChange={field.onChange}
                        onBlur={field.onBlur}
                        name={field.name}
                        ref={field.ref}
                        data-testid="input-location"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="contributorName"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>{t('knowledge.contributorName') || 'Your Name'} *</FormLabel>
                    <FormControl>
                      <Input 
                        placeholder={t('knowledge.contributorNamePlaceholder') || 'Your name for attribution'}
                        {...field}
                        data-testid="input-contributor-name"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="imageUrl"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>{t('knowledge.imageUrl') || 'Image URL (Optional)'}</FormLabel>
                  <FormControl>
                    <Input 
                      placeholder={t('knowledge.imageUrlPlaceholder') || 'Link to plant image'}
                      value={field.value || ""}
                      onChange={field.onChange}
                      onBlur={field.onBlur}
                      name={field.name}
                      ref={field.ref}
                      data-testid="input-image-url"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex items-center justify-between pt-4">
              {onClose && (
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={onClose}
                  data-testid="button-cancel"
                >
                  {t('common.cancel') || 'Cancel'}
                </Button>
              )}

              <Button 
                type="submit" 
                disabled={isSubmitting}
                className="ml-auto"
                data-testid="button-submit"
              >
                {isSubmitting ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2" />
                    {t('knowledge.submitting') || 'Submitting...'}
                  </>
                ) : (
                  <>
                    <Save className="h-4 w-4 mr-2" />
                    {t('knowledge.submit') || 'Submit Knowledge'}
                  </>
                )}
              </Button>
            </div>
          </form>
        </Form>
      </CardContent>
    </Card>
  );
}

server/csv-loader.ts.content:
import { readFileSync } from 'fs';
import { join } from 'path';

export interface PlantRecord {
  name: string;
  scientific_name: string;
  description: string;
  uses: string;
  preparation: string;
  location: string;
  image_url: string;
  family: string;
  parts_used: string;
  properties: string;
  precautions: string;
  hindi_name: string;
  sanskrit_name: string;
  english_name: string;
  regional_names: string;
  chemical_compounds: string;
  therapeutic_actions: string;
  dosage: string;
  season: string;
  habitat: string;
}

let plantDatabase: PlantRecord[] = [];
let isLoaded = false;

export function loadPlantDatabase(): PlantRecord[] {
  if (isLoaded && plantDatabase.length > 0) {
    return plantDatabase;
  }

  try {
    const csvPath = join(process.cwd(), 'server/data/expanded-medicinal-plants.csv');
    const csvContent = readFileSync(csvPath, 'utf-8');

    const lines = csvContent.split('\n');
    const headers = lines[0].split(',');

    plantDatabase = [];

    for (let i = 1; i < lines.length; i++) {
      const line = lines[i].trim();
      if (!line) continue;

      // Parse CSV line with proper handling of commas in quoted fields
      const values = parseCSVLine(line);
      if (values.length < headers.length) continue;

      const plant: PlantRecord = {
        name: values[0] || '',
        scientific_name: values[1] || '',
        description: values[2] || '',
        uses: values[3] || '',
        preparation: values[4] || '',
        location: values[5] || '',
        image_url: values[6] || '',
        family: values[7] || '',
        parts_used: values[8] || '',
        properties: values[9] || '',
        precautions: values[10] || '',
        hindi_name: values[11] || '',
        sanskrit_name: values[12] || '',
        english_name: values[13] || '',
        regional_names: values[14] || '',
        chemical_compounds: values[15] || '',
        therapeutic_actions: values[16] || '',
        dosage: values[17] || '',
        season: values[18] || '',
        habitat: values[19] || ''
      };

      plantDatabase.push(plant);
    }

    isLoaded = true;
    console.log(`Loaded ${plantDatabase.length} plants from database`);
    return plantDatabase;

  } catch (error) {
    console.error('Error loading plant database:', error);
    return [];
  }
}

function parseCSVLine(line: string): string[] {
  const result: string[] = [];
  let current = '';
  let inQuotes = false;

  for (let i = 0; i < line.length; i++) {
    const char = line[i];

    if (char === '"') {
      inQuotes = !inQuotes;
    } else if (char === ',' && !inQuotes) {
      result.push(current.trim());
      current = '';
    } else {
      current += char;
    }
  }

  result.push(current.trim());
  return result;
}

export function searchPlantsByName(query: string): PlantRecord[] {
  const database = loadPlantDatabase();
  const searchTerm = query.toLowerCase();

  return database.filter(plant => 
    plant.name.toLowerCase().includes(searchTerm) ||
    plant.scientific_name.toLowerCase().includes(searchTerm) ||
    plant.english_name.toLowerCase().includes(searchTerm) ||
    plant.hindi_name.toLowerCase().includes(searchTerm) ||
    plant.sanskrit_name.toLowerCase().includes(searchTerm)
  ).slice(0, 20); // Limit results
}

export function searchPlantsByUse(query: string): PlantRecord[] {
  const database = loadPlantDatabase();
  const searchTerm = query.toLowerCase();

  return database.filter(plant => 
    plant.uses.toLowerCase().includes(searchTerm) ||
    plant.therapeutic_actions.toLowerCase().includes(searchTerm) ||
    plant.description.toLowerCase().includes(searchTerm)
  ).slice(0, 20); // Limit results
}

export function getRandomPlants(count: number = 10): PlantRecord[] {
  const database = loadPlantDatabase();
  const shuffled = [...database].sort(() => 0.5 - Math.random());
  return shuffled.slice(0, count);
}

export function getPlantByName(name: string): PlantRecord | undefined {
  const database = loadPlantDatabase();
  return database.find(plant => 
    plant.name.toLowerCase() === name.toLowerCase() ||
    plant.scientific_name.toLowerCase() === name.toLowerCase()
  );
}

